import { t, Trans } from "@lingui/macro";
import { Box, Link, Theme, Typography } from "@mui/material";
import { makeStyles } from "@mui/styles";
import NextLink from "next/link";
import { forwardRef, ReactNode } from "react";

import Flex, { FlexProps } from "@/components/flex";
import contentRoutes from "@/content-routes.json";
import { BUILD_COMMIT, BUILD_GITHUB_REPO, BUILD_VERSION } from "@/domain/env";
import { useLocale } from "@/locales/use-locale";

const Version = () => {
  let commitLink = null;

  if (BUILD_GITHUB_REPO && BUILD_COMMIT) {
    commitLink = (
      <>
        (
        <Link
          color="primary"
          href={`${BUILD_GITHUB_REPO}/commit/${BUILD_COMMIT}`}
        >
          {BUILD_COMMIT.substr(0, 7)}
        </Link>
        )
      </>
    );
  } else if (BUILD_COMMIT) {
    commitLink = `(${BUILD_COMMIT.substr(0, 7)})`;
  }

  return (
    <>
      {BUILD_VERSION} {commitLink}
    </>
  );
};

const useStyles = makeStyles((theme: Theme) => ({
  root: {
    backgroundColor: theme.palette.grey[200],
    borderTopWidth: "1px",
    borderTopStyle: "solid",
    borderTopColor: theme.palette.grey[100],
  },
  logoContainer: {
    padding: `${theme.spacing(5)} ${theme.spacing(4)}`,
    borderTopWidth: "1px",
    borderBottomWidth: "1px",
    borderTopStyle: "solid",
    borderBottomStyle: "solid",
    borderTopColor: theme.palette.divider,
    borderBottomColor: theme.palette.divider,
  },
}));

export const Footer = ({ sx }: { sx?: FlexProps["sx"] }) => {
  const locale = useLocale();
  const classes = useStyles();
  return (
    <Flex
      component="footer"
      className={classes.root}
      sx={{
        flexDirection: ["column", "row"],
        justifyContent: ["flex-start", "space-between"],
        alignItems: ["flex-start", "center"],
        ...sx,
      }}
    >
      <Box
        sx={{
          width: ["100%", "auto"],
          px: [4, 0],
          py: 5,
          color: ["grey.900", "grey.700"],
        }}
      >
        <FooterLink
          href={t({
            id: "footer.institution.url",
            message: "https://www.bafu.admin.ch/bafu/en/home.html",
          })}
        >
          <Trans id="footer.institution.name">
            Federal Office for the Environment FOEN
          </Trans>
        </FooterLink>
      </Box>
      <Flex
        sx={{
          width: ["100%", "auto"],
          flexDirection: ["column", "row"],
          alignItems: ["flex-start", "center"],
        }}
      >
        <Box
          sx={{
            display: ["block", "none"],

            width: ["100%", "auto"],
            mx: [2, 0],
            mb: [4],
          }}
        >
          <Logo />
        </Box>
        <Flex
          sx={{
            justifyContent: "flex-end",
            alignItems: ["flex-start", "center"],
            width: ["100%", "auto"],
            flexDirection: ["column", "row"],
            mt: [4, 0],
          }}
        >
          <Typography
            variant="body2"
            sx={{
              px: [4, 3],
              py: [3, 4],
              color: "secondary.main",
              order: [7, 0],
            }}
          >
            <Version />
          </Typography>
          <FooterLinkBottom href="https://github.com/visualize-admin/visualization-tool/blob/main/CHANGELOG.md">
            Changelog
          </FooterLinkBottom>
          <FooterLinkBottom href="https://www.youtube.com/channel/UCNK0uJTJ74kbv3jmNtZfgOw">
            <Trans id="footer.tutorials">Tutorials</Trans>
          </FooterLinkBottom>
          <FooterLinkBottom href="https://visualization-tool.status.interactivethings.io/">
            <Trans id="footer.status">Status</Trans>
          </FooterLinkBottom>
          <NextLink href="/statistics" passHref legacyBehavior>
            <FooterLinkBottom>
              <Trans id="footer.statistics">Statistics</Trans>
            </FooterLinkBottom>
          </NextLink>
          <NextLink
            href={contentRoutes.legal[locale].path}
            passHref
            locale={false}
            legacyBehavior
          >
            <FooterLinkBottom>
              {contentRoutes.legal[locale].title}
            </FooterLinkBottom>
          </NextLink>
          <NextLink
            href={contentRoutes.imprint[locale].path}
            passHref
            locale={false}
            legacyBehavior
          >
            <FooterLinkBottom>
              {contentRoutes.imprint[locale].title}
            </FooterLinkBottom>
          </NextLink>
        </Flex>
      </Flex>
    </Flex>
  );
};

const useFooterLinkStyles = makeStyles((theme: Theme) => ({
  root: {
    color: theme.palette.primary.main,
    fontSize: "0.875rem",
    textDecoration: "none",
    cursor: "pointer",
    "&:hover": {
      color: theme.palette.primary.hover,
    },
    "&:active": {
      color: theme.palette.primary.hover,
    },
    "&:disabled": {
      cursor: "initial",
      color: theme.palette.primary.disabled,
    },
  },
  bottomLink: {
    fontSize: "0.875rem",

    borderTopStyle: "solid",
    borderTopColor: theme.palette.divider,

    textDecoration: "none",
    cursor: "pointer",
    "&:hover": {
      color: theme.palette.primary.hover,
    },
    "&:active": {
      color: theme.palette.primary.hover,
    },
    "&:disabled": {
      cursor: "initial",
      color: theme.palette.primary.disabled,
    },
  },
}));

const FooterLink = ({
  children,
  ...props
}: {
  children: ReactNode;
  href?: string;
}) => {
  const classes = useFooterLinkStyles();
  return (
    <Link
      className={classes.root}
      {...props}
      sx={{
        width: ["100%", "auto"],
        px: [0, 3],
        py: [0, 4],
      }}
    >
      {children}
    </Link>
  );
};

const FooterLinkBottom = forwardRef<
  HTMLAnchorElement,
  { children: ReactNode; href?: string }
>(function FooterLinkBottom({ children, ...props }, ref) {
  const classes = useFooterLinkStyles();

  return (
    <Link
      ref={ref}
      {...props}
      color="primary"
      className={classes.bottomLink}
      sx={{
        px: [4, 3],
        py: [3, 4],
        borderTopWidth: ["1px", 0],
        width: ["100%", "auto"],
      }}
    >
      {children}
    </Link>
  );
});

